var contactButton=document.querySelector(".main-contacts a"),contactForm=document.querySelector(".modal-content"),close=document.querySelector(".modal-close");contactButton.addEventListener("click",function(t){t.preventDefault(),contactForm.classList.add("modal-content-show")}),close.addEventListener("click",function(t){t.preventDefault(),contactForm.classList.remove("modal-content-show")}),window.addEventListener("keydown",function(t){27==t.keyCode&&contactForm.classList.contains("modal-content-show")&&contactForm.classList.remove("modal-content-show")});